import React from 'react';

// Get response from weather Api

const key = '109440449518b43abeb4e646cc838d3f';
let data = '';

fetch(`https://api.openweathermap.org/data/2.5/weather?lat=55.7558&lon=37.6173&appid=${key}`)
.then(function(response) {
    return response.json()
}).then(function(data){
    data = data;
})

// Header

class Header extends React.Component{
    degrees = data.main.temp - 273;
    render(){
        return (
        <header className="header">
            <p className="header__city">Москва</p>
            <main className="header__main">
                <img src="assets/img/icons/sun.svg" className="header__img"></img>
                <p className="header__degrees">{this.degrees}°</p>
            </main>
            <p className="header__weather">Солнечно</p>
        </header>
        )
    }
};

// Main

class WeatherItem extends React.Component{
    sun = 'assets/img/icons/sun.svg'
    cloudy = 'assets/img/icons/sun.svg'
    rain = 'assets/img/icons/sun.svg'
    rainy = 'assets/img/icons/sun.svg'
    snowAndRain = 'assets/img/icons/sun.svg'
    snowflake = 'assets/img/icons/sun.svg'
    render(){
        return(
            <div className="weather__item weather__item--today">
                 <p className="weather__day">Сегодня</p>
                <img src={this.sun} className="weather__img"></img>
                <div className="weather__degrees">
                    <p className="weather__day-degrees">4°</p>
                    <p className="weather__night-degrees">3°</p>
                </div>
            </div>
        )
    }
};

class Main extends React.Component{
    render(){
        return (
            <main className="main">
                <p className="main__title">По дням</p>
                <div className="weather__items">
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                </div>
            </main>
        )
    }
};

// Footer

class FooterItem extends React.Component{
    render(){
        return(
            <div className="footer__item">
                <p className="item__title">{this.props.title}</p>
                <p className="item__text">{this.props.degrees}°</p>
            </div>
        )
    }
};

class FooterTime extends React.Component{
    render(){
        return(
            <div className="footer__item">
                <p className="item__title">{this.props.title}</p>
                <div className="item__wrapper">
                    <img src={this.props.path} className="item__img"></img>
                    <p className="item__text">{this.props.time}</p>
                </div>
            </div>
        )
    }
}
class Footer extends React.Component{
    render(){
        return (
            <footer className="footer">
                <p className="footer__title">Подробнее</p>
                <div className="footer__details">
                <div className="footer__items">
                    <FooterItem title='По ощущениям' degrees='2'/>
                    <FooterItem title='Влажность' degrees='73'/>
                    <FooterItem title='Видимость' degrees='10'/>
                    <FooterItem title='Давление' degrees='760,00'/>
                    <FooterItem title='Ветер' degrees='4'/>
                </div>
                <div className="footer__items footer__sunrise">
                    <FooterTime title='Восход' time='7:23' path='assets/img/icons/sun-up.svg'/>
                    <FooterTime title='Закат' time='18:42' path='assets/img/icons/sun-down.svg'/>
                </div>
                </div>
            </footer>
        )
    }
};


class App extends React.Component{
  render(){
    return(
      <><Header />
      <Main/>
      <Footer/></>
    )
  }
};


export default App
