import React from 'react';

class Header extends React.Component{
  render(){
    return (
      <header className="header">
          <p className="header__city">Москва</p>
          <main className="header__main">
            <p className="header__degrees">4°</p>
          </main>
          <p className="header__weather">Солнечно</p>
      </header>
    )
  }
};

class Main extends React.Component{
  render(){
    return (
      <main class="main">
        <p class="main__title">По дням</p>
        <div class="weather__items">

        </div>
      </main>
    )
  }
};

class FooterItem extends React.Component{
    render(){
        return(
            <div class="footer__item">
                <p class="item__title">{this.props.title}</p>
                <p class="item__text">{this.props.degrees}°</p>
            </div>
        )
    }
}
class Footer extends React.Component{
    render(){
        return (
            <footer className="footer">
                <p className="footer__title">Подробнее</p>
                <div className="footer__details">
                <div class="footer__items">
                    <FooterItem title='По ощущениям' degrees='2'/>
                </div>
                <div class="footer__items">

                </div>
                </div>
            </footer>
        )
    }
};


class App extends React.Component{
  render(){
    return(
      <><Header />
      <Main/>
      <Footer/></>
    )
  }
};

