export const weatherForm = (event) => {
    if(event.target.value){
      document.querySelector('.weather__submit').classList.remove('invisible');
      console.log('1');
    } else {
      document.querySelector('.weather__submit').classList.add('invisible');
      console.log('2');
    }
  }
