import { func } from 'prop-types';
import React from 'react';
import * as ReactDomClient from 'react-dom/client';

class Header extends React.Component{
  render(){
    return (
      <header className="header">
          <p className="header__city">Москва</p>
          <main className="header__main">
            <p className="header__degrees">4°</p>
          </main>
          <p className="header__weather">Солнечно</p>
      </header>
    )
  }
};

class Main extends React.Component{
  render(){
    return (
      <main class="main">
        <p class="main__title">По дням</p>
        <div class="weather__items">

        </div>
      </main>
    )
  }
};


class App extends React.Component{
  render(){
    return(
      <><Header /><Header /><Header /></>
    )
  }
};

const root = ReactDomClient.createRoot(document.getElementById('container'));
root.render(<App/>);
