import React from 'react';

// Get response from weather Api

const key = '109440449518b43abeb4e646cc838d3f';
let data = '';

fetch(`https://api.openweathermap.org/data/2.5/weather?lat=55.7558&lon=37.6173&appid=${key}`)
.then(function(response) {
    return response.json()
}).then(function(data){
    data = data;
})

// Header

class Header extends React.Component{
    degrees = data.main.temp - 273;
  render(){
    return (
      <header className="header">
          <p className="header__city">Москва</p>
          <main className="header__main">
            <img src="assets/img/icons/sun.svg" className="header__img"></img>
            <p className="header__degrees">{this.degrees}°</p>
          </main>
          <p className="header__weather">Солнечно</p>
      </header>
    )
  }
};

class WeatherItem extends React.Component{
    sun = 'assets/img/icons/sun.svg'
    cloudy = 'assets/img/icons/sun.svg'
    rain = 'assets/img/icons/sun.svg'
    rainy = 'assets/img/icons/sun.svg'
    snowAndRain = 'assets/img/icons/sun.svg'
    snowflake = 'assets/img/icons/sun.svg'
    render(){
        return(
            <div className="weather__item weather__item--today">
                 <p className="weather__day">Сегодня</p>
                <img src={this.sun} className="weather__img"></img>
                <div className="weather__degrees">
                    <p className="weather__day-degrees">4°</p>
                    <p className="weather__night-degrees">3°</p>
                </div>
            </div>
        )
    }
};

class Main extends React.Component{
    render(){
        return (
            <main className="main">
                <p className="main__title">По дням</p>
                <div className="weather__items">
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                    <WeatherItem/>
                </div>
            </main>
        )
    }
};







class App extends React.Component{
  render(){
    return(
      <><Header />
      <Main/>
      <Footer/></>
    )
  }
};


export default App
