import React from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Footer from './components/Footer';

const API_KEY = '109440449518b43abeb4e646cc838d3f';

class App extends React.Component{
  state = {
    temp: undefined,
    city: undefined,
    sunrise: undefined,
    sunset: undefined,
    error: undefined,
    }

  gettingWeather = async () => {
    const api_url = await fetch(`api.openweathermap.org/data/2.5/weather?q=Moscow&appid=109440449518b43abeb4e646cc838d3f&units=metric`);
    const data = await api_url.json();
    console.log(data);
    this.setState({
      temp: data.main.temp,
      city: data.name,
      sunrise: undefined,
      sunset: undefined,
      error: undefined,
    })
  }


  render(){
    return(
      <><Header weather = {this.gettingWeather}/>
      <Main/>
      <Footer/></>
    )
  }
};


export default App
