import React from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Footer from './components/Footer';

const API_KEY = '109440449518b43abeb4e646cc838d3f';

class App extends React.Component{
  state = {
    temp: undefined,
    weatherDesc: undefined,
    feelsLike: undefined,
    humidity: undefined,
    pressure: undefined,
    tempMax: undefined,
    tempMin: undefined,
    city: undefined,
    sunrise: undefined,
    sunset: undefined,
    visibility: undefined,
    windSpeed: undefined,
    error: undefined,
    }

  gettingWeather = async () => {
    const api_url = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=Moscow&appid=${API_KEY}&units=metric`);
    const data = await api_url.json();
    console.log(data);
    this.setState({
      temp: Math.floor(data.main.temp),
      weatherDesc: data.weather[0].description,
      feelsLike: data.main.feels_like,
      humidity: data.main.humidity,
      pressure: data.main.pressure,
      tempMax: Math.floor(data.main.temp_max),
      tempMin: Math.floor(data.main.temp_min),
      city: data.name,
      sunrise: data.sys.sunrise,
      sunset: data.sys.sunset,
      visibility: data.visibility,
      windSpeed: data.wind.speed,
      error: undefined,
    })
  }


  render(){
    return(
      <><Header weather = {this.gettingWeather} city = {this.state.city} temp = {this.state.temp} weatherDesc = {this.state.weatherDesc}/>
      <Main tempMax = {this.state.tempMax} tempMin = {this.state.tempMin}/>
      <Footer/></>
    )
  }
};


export default App
