import { func } from 'prop-types';
import React from 'react';

const key = '109440449518b43abeb4e646cc838d3f';
let data = '';

fetch(`https://api.openweathermap.org/data/2.5/weather?lat=55.7558&lon=37.6173&appid=${key}`)
.then(function(response) {
    return response.json()
}).then(function(data){
    data = data;
}).then(()=>{
    class Header extends React.Component{
        degrees = data.main.temp - 273;
      render(){
        return (
          <header className="header">
              <p className="header__city">Москва</p>
              <main className="header__main">
                <img src="assets/img/icons/sun.svg" className="header__img"></img>
                <p className="header__degrees">{this.degrees}°</p>
              </main>
              <p className="header__weather">Солнечно</p>
          </header>
        )
      }
    };
})



export default Header
