import React from 'react';

class Header extends React.Component{
  render(){
    return (
      <header className="header">
          <p className="header__city">Москва</p>
          <main className="header__main">
            <img src="assets/img/icons/sun.svg" className="header__img"></img>
            <p className="header__degrees">4°</p>
          </main>
          <p className="header__weather">Солнечно</p>
      </header>
    )
  }
};

class WeatherItem extends React.Component{
    render(){
        return(
            <div className="weather__item weather__item--today">
                 <p className="weather__day">Сегодня</p>
                <img src="assets/img/icons/sun.svg" alt="Sun" className="weather__img"></img>
                <div className="weather__degrees">
                    <p class="weather__day-degrees">4°</p>
                    <p class="weather__night-degrees">3°</p>
                </div>
            </div>
        )
    }
}


class Main extends React.Component{
  render(){
    return (
      <main class="main">
        <p class="main__title">По дням</p>
        <div class="weather__items">

        </div>
      </main>
    )
  }
};

class FooterItem extends React.Component{
    render(){
        return(
            <div class="footer__item">
                <p class="item__title">{this.props.title}</p>
                <p class="item__text">{this.props.degrees}°</p>
            </div>
        )
    }
}
class Footer extends React.Component{
    render(){
        return (
            <footer className="footer">
                <p className="footer__title">Подробнее</p>
                <div className="footer__details">
                <div className="footer__items">
                    <FooterItem title='По ощущениям' degrees='2'/>
                    <FooterItem title='Влажность' degrees='73'/>
                    <FooterItem title='Видимость' degrees='10'/>
                    <FooterItem title='Давление' degrees='760,00'/>
                    <FooterItem title='Ветер' degrees='4'/>
                </div>
                <div className="footer__items footer__sunrise">
                    <FooterItem title='Восход' degrees='7:23'/>
                    <FooterItem title='Закат' degrees='18:42'/>
                </div>
                </div>
            </footer>
        )
    }
};


class App extends React.Component{
  render(){
    return(
      <><Header />
      <Main/>
      <Footer/></>
    )
  }
};


export default App
