import React from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Footer from './components/Footer';

const API_KEY = '109440449518b43abeb4e646cc838d3f';

class App extends React.Component{
  state = {
    temp: undefined,
    weatherDesc: undefined,
    feelsLike: undefined,
    humidity: undefined,
    pressure: undefined,
    tempMax: undefined,
    tempMin: undefined,
    city: undefined,
    sunrise: undefined,
    sunset: undefined,
    visibility: undefined,
    windSpeed: undefined,
    error: undefined,
    }

  gettingWeather = async () => {
    const api_url = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=Moscow&appid=${API_KEY}&units=metric`);
    const data = await api_url.json();
    let sunset = data.sys.sunset;
    let date = new Date();
    date.setTime(sunset);
    let sunsetDate = date.getHours() + ':' + date.getMinutes() + ':' + date.getSeconds();

    this.setState({
      temp: Math.floor(data.main.temp),
      weatherDesc: data.weather[0].description,
      city: data.name,
      tempMax: Math.floor(data.main.temp_max),
      tempMin: Math.floor(data.main.temp_min),
      feelsLike: Math.floor(data.main.feels_like),
      humidity: data.main.humidity,
      pressure: data.main.pressure,
      sunrise: data.sys.sunrise,
      sunset: sunsetDate,
      visibility: data.visibility,
      windSpeed: Math.floor(data.wind.speed),
      error: undefined,
    })
  }


  render(){
    return(
      <><Header weather = {this.gettingWeather}
      city = {this.state.city}
      temp = {this.state.temp}
      weatherDesc = {this.state.weatherDesc}/>

      <Main weather = {this.gettingWeather}
      tempMax = {this.state.tempMax}
      tempMin = {this.state.tempMin}
      weatherDesc = {this.state.weatherDesc}/>

      <Footer weather = {this.gettingWeather}
      feelsLike = {this.state.feelsLike}
      humidity = {this.state.humidity}
      pressure = {this.state.pressure}
      sunrise = {this.state.sunrise}
      sunset = {this.state.sunset}
      visibility = {this.state.visibility}
      windSpeed = {this.state.windSpeed}/></>
    )
  }
};


export default App
