import React from "react";


const API_KEY = '109440449518b43abeb4e646cc838d3f';

class Header extends React.Component{
    state = {
        temp: undefined,
        city: undefined,
        sunrise: undefined,
        sunset: undefined,
        error: undefined,
        }
    gettingWeather = async () => {
        const api_url = await fetch(`api.openweathermap.org/data/2.5/weather?q=Moscow&appid=${API_KEY}&units=metric`);
        const data = await api_url.json();
        console.log(data);
        this.setState({
            temp: data.main.temp,
        });
      }
    degrees = 4;
    render(){
        return (
        <header className="header">
            <p className="header__city">Москва</p>
            <main className="header__main">
                <img src="assets/img/icons/sun.svg" className="header__img"/>
                <p className="header__degrees">{this.degrees}°</p>
            </main>
            <p className="header__weather">Солнечно</p>
        </header>
        )
    }
};

export default Header
